import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get('/api/orders'); // Asegúrate de que esta URL sea correcta
        setOrders(res.data); // res.data debe ser un array de órdenes
      } catch (error) {
        console.error(error);
      }
    };

    fetchOrders();
  }, []);

  return (
    <div>
      <h1>Orders</h1>
      <ul>
        {orders.items.length === 0 ? (
          <p>No orders yet</p>
        ) : (
          orders.map(order => (
            <li key={order._id}>
              <strong>Table {order.tableNumber}</strong> - Status: {order.status}
              <ul>
                {order.items && order.items.length > 0 ? (
                  order.items.map(item => (
                    <li key={item._id}>{item.name || `Item ID: ${item._id}`}</li>
                  ))
                ) : (
                  <li>No items in this order</li>
                )}
              </ul>
            </li>
          ))
        )}
      </ul>
      <button onClick={() => navigate('/createOrder')}>Create order</button>
    </div>
  );
};

export default Orders;
