import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { orders } from './Orders';

const ModifyOrder = () => {
  const [tableNumber, setTableNumber] = useState('');
  const [items, setItems] = useState([]); // Selected item IDs
  const [availableItems, setAvailableItems] = useState([]); // Available items list
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const { orderId } = useParams(); // Get the order ID from URL params

  // Fetch order details and available items
useEffect(() => {
  const fetchOrderDetails = async () => {
    setLoading(true);
    try {
      const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:3000';
      const orderResponse = await axios.get(`${baseUrl}/api/orders/getOrdersById/${orderId}`);
      const itemsResponse = await axios.get(`${baseUrl}/api/Menu`);

      setTableNumber(orderResponse.data.tableNumber);
      setItems(orderResponse.data.items.map(item => item._id)); // Extracts item IDs
      // Ensure that the response is an array
      const fetchedItems = Array.isArray(itemsResponse.data) ? itemsResponse.data : [];
      setAvailableItems(fetchedItems); // Set available items as an array

    } catch (err) {
      console.error(err);
      setError('Failed to fetch order details.');
    } finally {
      setLoading(false);
    }
  };

  fetchOrderDetails();
}, [orderId]);


  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:3000';
      await axios.put(`${baseUrl}/api/orders/modifyOrder/${orderId}`, {
        tableNumber,
        items,
      });
      alert('Order modified successfully!');
      navigate('/orders');
    } catch (err) {
      console.error(err);
      setError('Failed to modify order.');
    } finally {
      setLoading(false);
    }
  };

  // Handle item selection
  const handleItemChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setItems([...items, value]); // Add selected item
    } else {
      setItems(items.filter((item) => item !== value)); // Remove unselected item
    }
  };

  return (
    <div>
      <h2>Modify Order</h2>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form id="modify-order-form" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="table-number">Table Number:</label>
          <input
            type="number"
            id="table-number"
            name="tableNumber"
            value={tableNumber}
            onChange={(e) => setTableNumber(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Items:</label>
          <ul>
          orders.map(order => (
            <li key={order._id}>
              <strong>Table {order.tableNumber}</strong> - Status: {order.status}
              <ul>
                {order.items && order.items.length > 0 ? (
                  order.items.map(item => (
                    <li key={item._id}>{item.name || `Item ID: ${item._id}`}</li>
                  ))
                ) : (
                  <li>No items in this order</li>
                )}
              </ul>
              </li>
            </ul>
        </div>

        <div>
          <button type="submit" id="submit-button" disabled={loading}>
            {loading ? 'Submitting...' : 'Modify Order'}
          </button>
          <button
            type="button"
            id="cancel-button"
            onClick={() => navigate('/orders')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ModifyOrder;
