import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const ModifyOrder = () => {
  const [tableNumber, setTableNumber] = useState('');
  const [items, setItems] = useState([{ item: '', quantity: 1 }]); // Cambiado a array de objetos con item y quantity
  const [availableItems, setAvailableItems] = useState([]); // Available items list
  const [status, setStatus] = useState(''); // Status of the order
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const { orderId } = useParams(); // Get the order ID from URL params

  // Fetch order details and available items
  useEffect(() => {
    const fetchOrderDetails = async () => {
      setLoading(true);
      try {
        const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:3000';
        const orderResponse = await axios.get(`${baseUrl}/api/orders/getOrdersById/${orderId}`);
        const itemsResponse = await axios.get(`${baseUrl}/api/menu`); // Assuming /api/menu provides available items

        setTableNumber(orderResponse.data.tableNumber);
        setItems(orderResponse.data.items.map(item => ({ item: item.item._id, quantity: item.quantity }))); // Set the items already in the order

        setAvailableItems(itemsResponse.data.items); // Set the available items

      } catch (err) {
        console.error(err);
        setError('Failed to fetch order details.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:3000';
      await axios.put(`${baseUrl}/api/orders/modifyOrder/${orderId}`, {
        tableNumber,
        items,
        status, // Include status in the request body
      });
      alert('Order modified successfully!');
      navigate('/orders');
    } catch (err) {
      console.error(err);
      setError('Failed to modify order.');
    } finally {
      setLoading(false);
    }
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;

    // Verificar si la cantidad es 0 y eliminar el ítem si es así
    if (field === 'quantity' && parseInt(value) === 0) {
      setItems(newItems.filter((_, idx) => idx !== index));
    } else {
      setItems(newItems);
    }
  };

  const handleItemCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setItems([...items, { item: value, quantity: 1 }]); // Add selected item with default quantity 1
    } else {
      setItems(items.filter((item) => item.item !== value)); // Remove unselected item by item ID
    }
  };


  return (
    <div>
      <h2>Modify Order</h2>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form id="modify-order-form" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="table-number">Table Number:</label>
          <input
            type="number"
            id="table-number"
            name="tableNumber"
            value={tableNumber}
            onChange={(e) => setTableNumber(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Items in this order:</label>
          {items.map((item, index) => {
            const menuItem = availableItems.find((i) => i._id === item.item);
            return menuItem && (
              <div key={index}>
                <label>
                {menuItem.name} <style>{`margin-bottom: 10px; display: block;`}</style>
                </label>
                <input 
                  type="number"
                  value={item.quantity}
                  onChange={(e) => handleItemChange(index, 'quantity', e.target.value)}
                  min="0"
                  required
                />
              </div>
            );
          })}
        </div>

        <div>
          <label htmlFor="order-status">Status:</label>
          <select
            id="order-status"
            name="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          >
            <option value="pending">Pending</option>
            <option value="in progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
        </div>

        <div>
          <button type="submit" id="submit-button" disabled={loading}>
            {loading ? 'Submitting...' : 'Modify Order'}
          </button>
          <button
            type="button"
            id="cancel-button"
            onClick={() => navigate('/orders')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ModifyOrder;
