import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true); // Inicia el estado de carga
      setError(null); // Resetea errores previos
      try {
        const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:3000';
        const res = await axios.get(`${baseUrl}/api/orders/getOrder`); // Asegúrate de usar la ruta correcta
        setOrders(res.data); // Actualiza las órdenes
      } catch (error) {
        console.error(error); // Muestra el error en consola
        setError(
          error.response?.data?.error || 
          "An unexpected error occurred while fetching orders."
        ); // Guarda el mensaje de error
      } finally {
        setLoading(false); // Finaliza el estado de carga
      }
    };

    fetchOrders();
  }, []);

return (
    <div>
      <h1>Orders</h1>
      <ul>
        {orders.length === 0 ? (
          <p>No orders yet</p>
        ) : (
          orders.map(order => (
            <li key={order._id}>
              <strong>Table {order.tableNumber}</strong> - Status: {order.status}
              <ul>
                {order.items && order.items.length > 0 ? (
                  order.items.map(item => (
                    <li key={item._id}>{item.name || `Item ID: ${item._id}`}</li>
                  ))
                ) : (
                  <li>No items in this order</li>
                )}
              </ul>
            </li>
          ))
        )}
      </ul>
      <button onClick={() => navigate('/createOrder')}>Create order</button>
      <button onClick={() => navigate('/menu')}>Orders</button>
    </div>
  );
};

export default Orders;
